mapper.select:
select count(*)
from memberinfo
where userid = #{userid} 
and passwd = #{passwd}

mapper.insert:
insert into memberinfo
	    ( 
			userid
		  , username
		  , passwd
		  , post
		  , addr1
		  , addr2
		  , phone
		  , birth
		  , email
		  , licensetype
		  , licensenum
		  , mlevel 
		) 
 values 
        (  
     		#{userid}
		  , #{username}
		  , #{passwd}
		  , #{post}
		  , #{addr1}
		  , #{addr2}
		  , #{phone}
		  , #{birth}
		  , #{email}
		  , #{licensetype}
		  , #{licensenum}
		  , #{mlevel}
		)
